// Função do n8n: recebe items com json.output
const maxLen = 220;
const text = items[0].json.output;  // ou ajuste conforme seu input
const parts = [];
let remaining = text;

while (remaining.length > 0) {
  // pega até o limite
  const chunk = remaining.slice(0, maxLen);
  let splitPos = -1;

  // tenta dividir em dupla quebra de linha
  const dnPos = chunk.lastIndexOf('\n\n');
  if (dnPos !== -1) {
    splitPos = dnPos + 2; // inclui as quebras
  } else {
    // procura o último sinal de pontuação permitido
    const puncts = ['.', '!', '?', ':'];
    let lastP = -1;
    for (const p of puncts) {
      const pos = chunk.lastIndexOf(p);
      if (pos > lastP) lastP = pos;
    }
    if (lastP !== -1) {
      splitPos = lastP + 1; // inclui o sinal
    }
  }

  // se não encontrou posição "natural", corta no maxLen
  if (splitPos === -1) {
    splitPos = maxLen;
  }

  // adiciona o pedaço e remove da string restante
  parts.push(remaining.slice(0, splitPos).trim());
  remaining = remaining.slice(splitPos);
}

// retorna cada parte como um novo item
return parts.map(p => ({ json: { chunk: p } }));

