const phone = $('Dados do Webhook').first().json.data_user;

let comprador = null;
let linhaComprador = -1;

// Como nÃ£o tem header row, os dados vÃªm como array
if (sheets && sheets.length > 0) {
  for (let i = 0; i < sheets.length; i++) {
    const row = sheets[i];
