        Produto_Escolhido: row[5] || '',
        Data_Resgate: row[6] || ''
      };
      linhaComprador = i + 2; // +2 porque comeÃ§a na linha 2
      break;
    }
  }
}

return [{
  json: {
    phone: phone,
    message: $('Dados do Webhook').first().json.message,
    name: $('Dados do Webhook').first().json.name_user,
    isComprador: !!comprador,
